"use strict";console.log("Script started");var Navbar=function(){var navbar=document.querySelector(".nav-bar");var toggleBtn=document.querySelector(".nav-bar .nav-toggle");var homeLink=document.querySelector(".nav-bar .home-link");var navSmall=document.querySelector(".nav-bar .nav-small");var showNav=false;function init(){events()}function events(){window.addEventListener("scroll",handleScroll);toggleBtn.addEventListener("click",toggleNav)}function toggleNav(e){showNav=!showNav;homeLink.style.display=showNav?"none":"inline";navSmall.style.visibility=!showNav?"hidden":"visible";toggleBtn.classList.toggle("fa-bars");toggleBtn.classList.toggle("fa-angle-left")}function handleScroll(e){var opac=1-e.pageY/1000;opac=opac<0.8?0.8:opac;navbar.style.opacity=opac}return{init:init}}();var Intro=function(){var welcomeTxt=document.querySelector(".welcome");var hour=new Date().getHours();function init(){setGreeting()}function setGreeting(){if(hour<6){welcomeTxt.innerText="Good night,"}else if(hour<12){welcomeTxt.innerText="Good morning,"}else if(hour<12){welcomeTxt.innerText="Good afternoon,"}else{welcomeTxt.innerText="Good evening,"}}return{init:init}}();Navbar.init();Intro.init();